catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    if (newValue == 'my') {
        orderdata('loggedin');
    } else {
        orderdata(g_form.getValue('requested_for_user'));
    }


    function orderdata(id) {
        var gao = new GlideAjax('sakon_order_guide');
        gao.addParam('sysparm_name', 'neworderguidetype');
        gao.addParam('sysparm_sakon_id', id);
        gao.getXML(orderdataresponse);
    }

    function orderdataresponse(responsedata) {
//         alert('alert' + responsedata.responseXML.documentElement.getAttribute("answer"));
        respose = responsedata.responseXML.documentElement.getAttribute("answer").split(',');
        g_form.setDisplay('i_want_to_request', true);
        g_form.setMandatory('i_want_to_request', true);
        g_form.clearOptions('i_want_to_request');
        g_form.hideFieldMsg('i_want_to_request', true);
        g_form.addOption('i_want_to_request', '', '-- None --', 0);
        if (respose) {
            for (var i = 0; i < respose.length; i++) {
                if (respose[i].toString().indexOf('New') > -1) {
                    g_form.addOption('i_want_to_request', respose[i], 'Device with New Line', i + 1);
                } else if (respose[i].toString().indexOf('Upgrade') > -1) {
                    g_form.addOption('i_want_to_request', 'Upgrade Device', 'Upgrade Device', i + 1);
                } else if (respose[i].toString().indexOf('Port') > -1) {
                    g_form.addOption('i_want_to_request', respose[i], respose[i], i + 1);
                } else if (respose[i].toString().indexOf('Only') > -1) {
                    g_form.addOption('i_want_to_request', respose[i], respose[i], i + 1);
                }
            }
        } else {
			alert('ELSE');
            g_form.showFieldMsg('i_want_to_request', 'Requested For User Is Not-Eligible For Device/Service/Accessory Categories', 'error');
            g_form.addErrorMessage('Requested For User Is Not-Eligible For Device/Service/Accessory Categories');
        }
    }

    //Type appropriate comment here, and begin script below

}
