catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading) {
        if (g_form.getValue('accessories') != '') {
            g_form.setVisible('display_image_data', true);
            g_form.setVisible('display_image_data_price', true);
            g_form.setVisible('display_image', true);
            g_form.setValue('devices_or_accessory', true);

            g_form.setDisplay('carrier', true);
            g_form.setMandatory('carrier', true);
            accessoryimage(g_form.getValue('accessories'));
        } else {
            if (g_form.getValue('shopping_for') != '' && g_form.getValue('accessories') == '') {
                g_form.setValue('devices_or_accessory', false);
                g_form.setMandatory('devices_or_accessory', true);
            }
        }
        return;
    }

    g_form.setVisible('display_image_data', true);
    g_form.setVisible('display_image_data_price', true);
    g_form.setVisible('display_image', true);
    g_form.clearValue('display_image_data_price');
    g_form.clearValue('display_image_data');

    accessoryimage(newValue);
    g_form.setValue('devices_or_accessory', true);

    g_form.setDisplay('carrier', true);
    g_form.setMandatory('carrier', true);


    function accessoryimage(accessoryitemid) {
        var ga = new GlideAjax('sakon_order_guide');
        ga.addParam('sysparm_name', 'accessoryimage');
        ga.addParam('sysparm_accessoryitemid', accessoryitemid);
        ga.getXML(HelloWorldParse);
    }

    function HelloWorldParse(response) {
        // alert(response.responseXML.documentElement.getAttribute("answer"));
        var answer = response.responseXML.documentElement.getAttribute("answer").split(',');

        g_form.setValue('display_image_data', answer[0]);
        g_form.setValue('display_image_data_price', '$' + answer[1]);
        itamUpdateImage(answer[2] + ".iix");
    }

}
