catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading) {

        if (g_form.getValue('accessories') == '' && g_form.getValue('shopping_for') != '') {
            g_form.setValue('devices_or_accessory', false);
            g_form.setMandatory('devices_or_accessory', true);
        }else if(g_form.getValue('accessories') != '' && g_form.getValue('shopping_for') != ''){
			g_form.setValue('devices_or_accessory', true);
            g_form.setMandatory('devices_or_accessory', true);
		}
        return;
    }
    if (newValue == 'true') {
        if (g_form.getValue('accessories') == '') {
            g_form.setValue('devices_or_accessory', false);
            g_form.setMandatory('devices_or_accessory', true);
        }
    }else{
		if (g_form.getValue('accessories') != '') {
            g_form.setValue('devices_or_accessory', true);
            g_form.setMandatory('devices_or_accessory', true);
        }
	}

    //Type appropriate comment here, and begin script below

}
