catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
		g_form.setValue("first_name", g_user.firstName);
		g_form.setValue("last_name", g_user.lastName);
        return;
    } else {
        g_form.clearValue("different_shipping_address", true);
        g_form.setValue("contact_number", "0");
        if (newValue == "Americas") {
            g_form.setValue("country", "Americas");
            g_form.setValue("address_line_1", "20 Rockefeller Center, 5th Floor");
            g_form.setValue("city", "New York");
            g_form.setValue("state", "NY");
            g_form.setValue("zip_code", "10020");
            //g_form.setValue("country_id", "52123291");
            g_form.setValue("attention_name", "IT Support");
        } else if (newValue == "France") {
            g_form.setValue("select_region", "France");
            g_form.setValue("address_line_1", "9 Avenue Matignon");
            g_form.setValue("city", "Paris");
            g_form.setValue("state", "PAR");
            g_form.setValue("zip_code", "75008");
            //g_form.setValue("country", "594000105");
            g_form.setValue("attention_name", "IT Support");
        } else if (newValue == "Hong Kong") {
            g_form.setValue("country", "Hong Kong");
            g_form.setValue("address_line_1", "22/F, Alexandra House, Central");
            g_form.setValue("city", "Hong Kong");
            g_form.setValue("state", "HK");
            g_form.setValue("zip_code", "999077");
            //g_form.setValue("country_id", "594135407");
            g_form.setValue("attention_name", "IT Support");
        } else if (newValue == "Switzerland (Geneva)") {
            g_form.setValue("country", "Switzerland (Geneva)");
            g_form.setValue("address_line_1", "8 Place de la Taconnerie");
            g_form.setValue("city", "Genève");
            g_form.setValue("state", "GNV");
            g_form.setValue("zip_code", "1204");
            //g_form.setValue("country_id", "52258589");
            g_form.setValue("attention_name", "IT Support");
        } else if (newValue == "Switzerland (Zurich)") {
            g_form.setValue("country", "Switzerland (Zurich)");
            g_form.setValue("address_line_1", "26 Steinwiesstrasse");
            g_form.setValue("city", "Zürich");
            g_form.setValue("state", "ZH");
            g_form.setValue("zip_code", "8032");
            //g_form.setValue("country_id", "52258589");
            g_form.setValue("attention_name", "IT Support");
            //} else if (newValue == "EMEA") {
            //  g_form.setValue("country", "EMEA");
        } else if (newValue == "United Kingdom") {
            g_form.setValue("country", "United Kingdom");
            g_form.setValue("address_line_1", "8 King Street, St. James's");
            g_form.setValue("city", "London");
            g_form.setValue("state", "LDN");
            g_form.setValue("zip_code", "SW1Y 6QT");
            //g_form.setValue("country_id", "52123292");
            g_form.setValue("attention_name", "Praveen Iyar");
        } else if (newValue == "Other Region") {
            g_form.clearValue("country");
            g_form.clearValue("address_line_1");
            g_form.clearValue("city");
            g_form.clearValue("state");
            g_form.clearValue("zip_code");
            //g_form.clearValue("country_id");
            g_form.clearValue("attention_name");
        }
    }
}
