catalog_script_client.script:
function onLoad() {

	g_form.setMandatory('select_your_preferred_carrier',false);
	g_form.setDisplay('select_your_preferred_carrier',false);
	g_form.setMandatory('new_device_type',false);
	g_form.setDisplay('new_device_type',false);
	g_form.setMandatory('select_device_model',false);
	g_form.setDisplay('select_device_model',false);
	g_form.setDisplay('requested_by',false);
	g_form.setDisplay('order_for',false);
	g_form.setDisplay('service_category',false);
	g_form.setDisplay('phone_numbernew',false);
	g_form.setDisplay('upgrade_type',false);
	g_form.setDisplay('price',false);
	g_form.setReadOnly('requested_for', true);
	g_form.setDisplay('device_accessory', false);
	g_form.setDisplay('color', false);
	g_form.setDisplay('memory', false);
	g_form.setDisplay('services', false);
	g_form.setDisplay('i_want_to_request', false);



    var ga = new GlideAjax('x_sakon_mobile.sakon_order_guide');
    ga.addParam('sysparm_name', 'userisadmin');
    ga.getXML(userresponse);

    function userresponse(serverResponse) {
        var respose = serverResponse.responseXML.documentElement.getAttribute("answer");
        if (respose == 'YES') {
            g_form.setReadOnly('requested_for', false);
        }
    }
}
