catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    if (newValue) {
			//alert(g_form.getValue('order_for') + " ; " + g_form.getValue('select_device_model') + " ; " + g_form.getValue("phone_numbernew") + " ; " + g_form.getValue("service_category"));
			carrierinfo(g_form.getValue('select_device_model'), g_form.getValue('order_for'));
        }

        function carrierinfo(cat_item_id, order_type) {
            var ga = new GlideAjax('x_sakon_mobile.sakon_customization');
            ga.addParam('sysparm_name', 'getNetworkProviders');
            ga.addParam('cat_item_id', cat_item_id);
            ga.addParam('order_type', order_type);
            ga.getXML(carrierresponse);
        }

        function carrierresponse(serverResponse) {
            var answer = serverResponse.responseXML.documentElement.getAttribute("answer").split(',');
            //alert(answer.length + serverResponse.responseXML.documentElement.getAttribute("answer"));

            g_form.clearOptions('select_your_preferred_carrier');
            g_form.addOption('select_your_preferred_carrier', '', '-- None --', 0);
            for (var i = 0; i < answer.length; i++) {
                g_form.addOption('select_your_preferred_carrier', answer[i], answer[i]);
            }
        }
    }
