catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading) {
        if (g_form.getValue('order_for_user') != '') {
            userinfo(g_form.getValue('order_for_user'));
        }
        return;
    }
    if (newValue) {
        //alert(g_form.getValue('order_for_user'));
        userinfo(value);
    }

    function userinfo(value) {
        var ga = new GlideAjax('x_sakon_mobile.sakon_customization');
        ga.addParam('sysparm_name', 'getuserinfo');
        ga.addParam('sysparm_sakon_id', value);
        ga.getXML(userresponse);
    }

    function userresponse(serverResponse) {
        var answer = serverResponse.responseXML.documentElement.getAttribute("answer").split(',');
        //alert(answer.length + serverResponse.responseXML.documentElement.getAttribute("answer"));

        //fname-lname-phone-street-city-country-state-zip
        g_form.setValue('attention_name', answer[0] + ' ' + answer[1]);
        g_form.setValue('first_name', answer[0]);
        g_form.setValue('last_name', answer[1]);
        g_form.setValue('contact_number', answer[2]);
        g_form.setValue('address_line_1', answer[3]);
        g_form.setValue('address_line_2', answer[3]);
        g_form.setValue('city', answer[4]);
        g_form.setValue('country', answer[5]);
        g_form.setValue('state', answer[6]);
		g_form.setValue('state_province', answer[6]);
        g_form.setValue('zip_code', answer[7]);
		g_form.setValue('zip_postal_code', answer[7]);
        g_form.setValue('phone_number', answer[2]);
        g_form.setValue('new_employee_s_id_number', answer[9]);
        g_form.setValue('new_employee_s_corporate_email_address', answer[8]);
		g_form.setValue('employees_id_number', answer[9]);
		g_form.setValue('employees_corporate_mail_address', answer[8]);
    }

}
