catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {

        if (g_form.getValue('i_want_to_request') == 'Device Only') {
            g_form.setMandatory('phone_number', false);
            g_form.setDisplay('phone_number', false);

            g_form.setDisplay('phone', true);
            g_form.setDisplay('buttonaccessory', true);
            g_form.setMandatory('buttonaccessory', true);

            g_form.setValue('order_type', 'Device Only');
            g_form.setDisplay('order_type', false);

        } else if (g_form.getValue('i_want_to_request') == 'New Line') {

            g_form.setMandatory('phone_number', false);
            g_form.setDisplay('phone_number', false);

            g_form.setDisplay('buttonaccessory', true);
            g_form.setMandatory('buttonaccessory', true);

            g_form.setDisplay('phone', true);
            g_form.setValue('order_type', 'New Line');
            g_form.setDisplay('order_type', false);

        } else if (g_form.getValue('i_want_to_request') == 'Upgrade Device' || g_form.getValue('i_want_to_request') == 'Port Number') {

            getphonenumbers(g_form.getValue('requested_for_user'));
            g_form.setMandatory('buttonaccessory', false);
            g_form.setDisplay('buttonaccessory', false);

            g_form.setDisplay('phone', false);
            g_form.setDisplay('accessories', false);

            g_form.setDisplay('phone_number', true);
            g_form.setMandatory('phone_number', true);
        }
        return;
    }

    g_form.clearValue('accessories');
    g_form.clearValue('carrier');


    if (newValue == 'Device Only') {
        g_form.setMandatory('phone_number', false);
        g_form.setDisplay('phone_number', false);

        g_form.setDisplay('phone', true);
        g_form.setDisplay('buttonaccessory', true);
        g_form.setMandatory('buttonaccessory', true);

        //g_form.setDisplay('accessories', true);
        g_form.setValue('order_type', newValue);
        g_form.setDisplay('order_type', false);

    } else if (newValue == 'New Line') {

        g_form.setMandatory('phone_number', false);
        g_form.setDisplay('phone_number', false);

        g_form.setDisplay('buttonaccessory', true);
        g_form.setMandatory('buttonaccessory', true);

        g_form.setDisplay('phone', true);
        //         g_form.setDisplay('accessories', true);
        g_form.setValue('order_type', newValue);
        g_form.setDisplay('order_type', false);
    } else {
        getphonenumbers(g_form.getValue('requested_for_user'));
        g_form.setMandatory('buttonaccessory', false);
        g_form.setDisplay('buttonaccessory', false);

        g_form.setDisplay('phone', false);
        g_form.setDisplay('accessories', false);

        g_form.setDisplay('phone_number', true);
        g_form.setMandatory('phone_number', true);
    }

    function getphonenumbers(userid) {
        var ga = new GlideAjax('sakon_order_guide');
        ga.addParam('sysparm_name', 'getphonenumbersdata');
        ga.addParam('sysparm_userid', userid);
        ga.getXML(HelloWorldParse);
    }

    function HelloWorldParse(response) {
        var answer = response.responseXML.documentElement.getAttribute("answer").split(',');
        g_form.clearOptions('phone_number');
        g_form.clearMessages();
        g_form.addOption('phone_number', '', '-- None --', 0);
        if (answer) {
            for (var i = 0; i < answer.length; i++) {
                g_form.addOption('phone_number', answer[i], answer[i], i + 1);
            }
        } else {
            g_form.showFieldMsg('phone_number', 'No Assigned Service Lines for ' + g_form.getDisplayValue('requested_for_user'), 'error');
        }
    }

}
