catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        if (g_form.getValue('new_device_type') == '') {
            g_form.setMandatory('select_device_model', false);
            g_form.setDisplay('select_device_model', false);
        } else {
            deviceselection(g_form.getValue('requested_for'), 'Devices', g_form.getValue('new_device_type'));
        }
        return;
    }
    g_form.setMandatory('memory', false);
    g_form.setDisplay('memory', false);
    g_form.setMandatory('color', false);
    g_form.setDisplay('color', false);

    g_form.clearOptions('memory');
    g_form.clearOptions('color');
	g_form.clearValue('memory');
	g_form.clearValue('color');
	g_form.clearValue('select_device_model');
	g_form.setValue('order_for',g_form.getValue('selection_type'));
    deviceselection(g_form.getValue('requested_for'), 'Devices', newValue);
}

function deviceselection(user, type, subtype) {
    var ga = new GlideAjax('x_sakon_mobile.sakon_order_guide');
    ga.addParam('sysparm_name', 'getcolor');
    ga.addParam('sysparm_sakon_id', user);
    ga.addParam('sysparm_type_id', type);
    ga.addParam('sysparm_subtype_id', subtype);
    ga.getXML(deviceresponse);
}

function deviceresponse(serverResponse) {
    var respose = serverResponse.responseXML.documentElement.getAttribute("answer");

    var rawdata = respose.split(',');
    if (respose.toString().indexOf('color') > -1) {

        g_form.setDisplay('color', true);
        g_form.setMandatory('color', true);
        g_form.addOption('color', '', '-- None --', 0);

        for (var i = 0; i < rawdata.length; i++) {
            if (rawdata[i].toString().indexOf('color') > -1) {
                g_form.addOption('color', rawdata[i].split(':')[1], rawdata[i].split(':')[1], i + 1);
            }
        }
    } else if (respose.toString().indexOf('memory') > -1) {
        g_form.setDisplay('memory', true);
        g_form.setMandatory('memory', true);
        g_form.addOption('memory', '', '-- None --', 0);
        for (var k = 0; k < rawdata.length; k++) {
            if (rawdata[k].toString().indexOf('memory') > -1) {
                g_form.addOption('memory', rawdata[k].split(':')[1], rawdata[k].split(':')[1], k + 1);
            }
        }
    } else {
        g_form.setDisplay('select_device_model', true);
        g_form.setMandatory('select_device_model', true);
    }
}
