catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    checkupgradetype(newValue);

    g_form.setDisplay('phone', true);

    function checkupgradetype(phoneid) {
        var ga = new GlideAjax('sakon_order_guide');
        ga.addParam('sysparm_name', 'checkupgradetype');
        ga.addParam('sysparm_phoneid', phoneid);
        ga.getXML(HelloWorldParse);
    }

    function HelloWorldParse(response) {
        var answer = response.responseXML.documentElement.getAttribute("answer").split(',');
        //         alert(answer);
        g_form.clearMessages();
        if (answer[0] == 'Upgrade Eligible') {
            g_form.setValue('order_type', 'Upgrade Eligible');
            g_form.setDisplay('buttonaccessory', true);
            g_form.setMandatory('buttonaccessory', true);
        } else {
            g_form.setValue('order_type', 'Upgrade Full Price');
            g_form.showFieldMsg('phone_number', 'You are ordering a device at full price. The price will come down upon your upgrade date ' + answer[1].split(' ')[0] + '.');
            g_form.setDisplay('buttonaccessory', true);
            g_form.setMandatory('buttonaccessory', true);
        }
    }
}
