catalog_script_client.script:
function onLoad() {
    //Type appropriate comment here, and begin script below
    var ga = new GlideAjax('x_sakon_mobile.sakon_customization');
    ga.addParam('sysparm_name', 'userisApprover');
    ga.getXML(isApprover);

    function isApprover(serverResponse) {
        var answer = serverResponse.responseXML.documentElement.getAttribute("answer");
        if (answer == 'YES') {
            g_form.setVariablesReadOnly(true);
            g_form.setReadOnly('carrier', false);
			g_form.addOption('carrier', 'Verizon', 'Verizon');
            g_form.setReadOnly('note', false);
        } else {
            g_form.setVariablesReadOnly(true);
        }
    }

}
