catalog_script_client.script:
function onLoad() {
    //Type appropriate comment here, and begin script below

    g_form.setDisplay('requested_by_user', false);
    g_form.setDisplay('requested_for_user', false);
    g_form.setDisplay('shopping_for', true);
    g_form.setMandatory('shopping_for', true);
    g_form.setDisplay('select_service_required', false);
    g_form.setDisplay('accessories', false);
    g_form.setDisplay('display_image_data', false);
    g_form.setDisplay('display_image_data_price', false);
    g_form.setDisplay('display_image', false);
    g_form.setDisplay('buttonaccessory', false);
    g_form.setDisplay('devices_or_accessory', false);
    g_form.setDisplay('phone', false);
    g_form.setDisplay('rich_label', false);
    g_form.setMandatory('i_want_to_request', false);
    g_form.setDisplay('i_want_to_request', false);
    g_form.setDisplay('select_eligibility_level', false);
    g_form.setDisplay('phone_number', false);
    g_form.setDisplay('deviceonlybutton', false);
    g_form.setDisplay('newlinebutton', false);
    g_form.setDisplay('carrier', false);
    g_form.setDisplay('order_type', false);


    var ga = new GlideAjax('sakon_order_guide');
    ga.addParam('sysparm_name', 'userprivilege');
    ga.getXML(HelloWorldParse);


    function HelloWorldParse(response) {
        if (response.responseXML.documentElement.getAttribute("answer") == 'NO') {
            g_form.setValue('shopping_for', 'Myself');
            g_form.setReadOnly('shopping_for', true);
            g_form.setDisplay('phone', true);
            g_form.setMandatory('phone', true);
        }
    }

}
