catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    if (g_form.getValue('shopping_for') == 'SomeoneElse') {

        g_form.clearOptions('select_eligibility_level');
        g_form.setDisplay('select_eligibility_level', true);
        g_form.setMandatory('select_eligibility_level', true);
        g_form.addOption('select_eligibility_level', '', '-- None --', 0);
        g_form.addOption('select_eligibility_level', 'my', 'Use my eligibility level',1);
        g_form.addOption('select_eligibility_level', 'other', 'Use eligibility level of ' + g_form.getDisplayValue('requested_for_user'),2);
    } else {
        g_form.clearOptions('select_eligibility_level');

        g_form.setMandatory('select_eligibility_level', false);
        g_form.setDisplay('select_eligibility_level', false);
    }

    //Type appropriate comment here, and begin script below

}
