sys_script_fix.script:
var grGroupMember = new GlideRecordSecure('sys_user_group');
grGroupMember.addQuery('name', 'Sakon Mobile Access');
grGroupMember.query();
if (grGroupMember.next()) {
    var groupSysID = grGroupMember.sys_id;
}

var userGR = new GlideRecord('sys_user');
userGR.addEncodedQuery('active=true^employee_numberISNOTEMPTY^emailISNOTEMPTY');
//userGR.setLimit(1);
userGR.query();
while (userGR.next()) {
    var grpToAdd = new GlideRecord('sys_user_grmember');
    grpToAdd.addQuery('group', groupSysID);
    grpToAdd.addQuery('user', userGR.sys_id);
    grpToAdd.query();
    if (!grpToAdd.next()) {
        grpToAdd.initialize();
        grpToAdd.group = groupSysID;
        grpToAdd.user = userGR.sys_id;
        grpToAdd.insert();
    }
}
