function apiKeyCheck() {
    document.getElementById('lblApiKeySuccess').innerHTML = '';
    document.getElementById('lblApiKeyError').innerHTML = '';
    document.getElementById('lblApiKeyComments').innerHTML = '';
    var debugInfo = '\nAPI key check - Begin';
    var ga = new GlideAjax('SakonSmokeTest');
    ga.addParam('sysparm_name', 'getConfigurationData');
    ga.addParam('system_prop_name', 'x_sakon_mobile.api_key');
    ga.getXML(function(response) {
        callback(response, debugInfo);
    });

    function callback(response, debugInfo) {
        var answer = response.responseXML.documentElement.getAttribute("answer");
        answer = JSON.parse(answer);
        var apiKey = answer.value;
        debugInfo += '\nApi key value : ' + (apiKey === "" || apiKey === null ? apiKey : apiKey.substring(0, 20));

        if (apiKey.length > 0) {
            document.getElementById('lblApiKeySuccess').innerHTML = 'Success';
        } else {
            document.getElementById('lblApiKeyError').innerHTML = 'Error';
            $('#lblApiKeyComments').removeAttr('style');
            $('#lblApiKeyComments').attr('style', 'color:red');
        }
        debugInfo += '\nApi Key Check : End\n';
        document.getElementById('txtdebuginfo').innerHTML += debugInfo;
    }
}

