function marketplaceConnectivityCheck() {
    document.getElementById('mrKtplCcSuccess').innerHTML = '';
    document.getElementById('mrKtplCcError').innerHTML = '';
    document.getElementById('lblMarketplaceComments').innerHTML = '';
    var debugInfo = '\nMarketplace api connectivity check - Begin';
    var ga = new GlideAjax('SakonSmokeTest');
    ga.addParam('sysparm_name', 'getConnectivityStatus');
    ga.addParam('rest_message_name', 'Market place connectivity check');
    ga.getXML(function(response) {
        callback(response, debugInfo);
    });

    function callback(response, debugInfo) {
        var answer = response.responseXML.documentElement.getAttribute("answer");
        debugInfo += '\nMarketplace module ping response : ' + answer;
        if (answer == null) {
            document.getElementById('mrKtplCcError').innerHTML = 'Error';
            $('#lblMarketplaceComments').removeAttr('style');
            $('#lblMarketplaceComments').attr('style', 'color:red');
        } else {
            document.getElementById('mrKtplCcSuccess').innerHTML = 'Success';
        }
        debugInfo += '\nMarketplace  api connectivity check - End\n';
        document.getElementById('txtdebuginfo').innerHTML += debugInfo;
    }
}

