function servicesSyncCheck() {
    document.getElementById('lblServicesSyncSuccess').innerHTML = '';
    document.getElementById('lblServicesSyncError').innerHTML = '';
    document.getElementById('lblServiceSyncComments').innerHTML = '';
    var debugInfo = '\nServices sync status check - Begin';
    var ga = new GlideAjax('SakonSmokeTest');
    ga.addParam('sysparm_name', 'getServicesLastSyncDate');
    ga.getXMLAnswer(function(response) {
        answer = JSON.parse(response);
        var dateDiff = getDaysDifferenceNow(answer.lastsyncdate);
        if (dateDiff == 10000) {
            $('#lblServiceSyncComments').removeAttr('style');
            $('#lblServiceSyncComments').html("Sync process was never run.");
            $('#lblServiceSyncComments').attr('style', 'color:red');
        } else if (dateDiff > 2) {
            $('#lblServiceSyncComments').removeAttr('style');
            $('#lblServiceSyncComments').html("Last successful sync was " + Math.floor(dateDiff) + " days ago. Total services count: " + answer.count);
            $('#lblServiceSyncComments').attr('style', 'color:red');
        }
        debugInfo += '\nServices last synced on: ' + answer.lastsyncdate;
        debugInfo += '\nCount of services last synced : ' + answer.count;
        if (answer == null) {
            document.getElementById('lblServicesSyncError').innerHTML = 'Error';
        } else if (dateDiff >= 2) {
            document.getElementById('lblServicesSyncSuccess').innerHTML = 'Warning';
            $('#lblServicesSyncSuccess').attr('style', 'color:orange');

        } else {
            document.getElementById('lblServicesSyncSuccess').innerHTML = 'Success';
        }
        debugInfo += '\nServices sync status check - End\n';
        document.getElementById('txtdebuginfo').innerHTML += debugInfo;
    });
}

function devicesSyncCheck() {
    document.getElementById('lblDevicesSyncSuccess').innerHTML = '';
    document.getElementById('lblDevicesSyncError').innerHTML = '';
    document.getElementById('lbldeviceSyncComments').innerHTML = '';
    var debugInfo = '\nDevices sync status check - Begin';
