    ga.addParam('sysparm_name', 'getDevicesLastSyncDate');
    ga.addParam('x_sakon_mobile_devices_table', 'phone_number');
    ga.getXMLAnswer(function(answer) {
        answer = JSON.parse(answer);
        var dateDiff = getDaysDifferenceNow(answer.deviceslastsyncdate);
        if (dateDiff == 10000) {
            $('#lbldeviceSyncComments').removeAttr('style');
            $('#lbldeviceSyncComments').html("Sync process was never run.");
            $('#lbldeviceSyncComments').attr('style', 'color:red');
        } else if (dateDiff > 2) {
            $('#lbldeviceSyncComments').removeAttr('style');
            $('#lbldeviceSyncComments').html("Last sync happened : " + Math.floor(dateDiff) + " days ago. Total devices count: " + answer.count);
            $('#lbldeviceSyncComments').attr('style', 'color:red');
        }
        debugInfo += '\nDevices last synced on: ' + answer.deviceslastsyncdate;
        debugInfo += '\nCount of devices last synced : ' + answer.count;

        if (answer == null) {
            document.getElementById('lblDevicesSyncError').innerHTML = 'Error';
        } else if (dateDiff >= 2) {
            document.getElementById('lblDevicesSyncError').innerHTML = 'Warning';
            $('#lblDevicesSyncError').attr('style', 'color:orange');
        } else {
            document.getElementById('lblDevicesSyncSuccess').innerHTML = 'Success';
