    });
}

function marketplaceOrdersSyncCheck() {
    document.getElementById('lblMarketplaceSyncSuccess').innerHTML = '';
    document.getElementById('lblMarketplaceSyncError').innerHTML = '';
    document.getElementById('lblMarketplaceSyncComments').innerHTML = '';
    var debugInfo = '\nMarketplace sync status check - Begin';
    var ga = new GlideAjax('SakonSmokeTest');
    ga.addParam('sysparm_name', 'getMarketplaceLastSyncDate');
    ga.getXMLAnswer(function(answer) {
        answer = JSON.parse(answer);
        var daysDifference = getDaysDifferenceNow(answer.mrktplastsyncdate);
        var isError = 0;
        var isWarning = 0;
        var message = '';
        if (daysDifference == 10000) {
            isWarning = 1;
            message = 'Sync process was never run.';
        } else if (daysDifference > 0) {
            isWarning = 1;

            message = "Last sync happened : " + Math.floor(daysDifference) + " days ago.";
			alert(message);
        } else {
            var dateDiff = getHoursDifferenceNow(answer.mrktplastsyncdate);
            if (dateDiff == 10000) {
                isError = 1;
                message = 'Sync process was never run.';
            } else if (dateDiff > 2) {
                isWarning = 1;
                message = "Last sync happened : " + Math.floor(dateDiff) + " hours ago.";
            }
        }
        debugInfo += '\nMarketplace last synced on: ' + answer.mrktplastsyncdate;
       // debugInfo += '\nCount of devices last synced : ' + answer.count;//Srikanth-commented because not required.
        if (answer == null) {
            document.getElementById('lblMarketplaceSyncError').innerHTML = 'Error';
        } else if (isError == 1) {
            document.getElementById('lblMarketplaceSyncError').innerHTML = 'Error';
            $('#lblMarketplaceSyncComments').removeAttr('style');
            $('#lblMarketplaceSyncComments').html(message);
            $('#lblMarketplaceSyncComments').attr('style', 'color:red');
        } else if (isWarning == 1) {
            document.getElementById('lblMarketplaceSyncError').innerHTML = 'Warning';
            $('#lblMarketplaceSyncError').attr('style', 'color:orange');
            $('#lblMarketplaceSyncComments').removeAttr('style');
            $('#lblMarketplaceSyncComments').html(message);
            $('#lblMarketplaceSyncComments').attr('style', 'color:red');
        } else {
            document.getElementById('lblMarketplaceSyncSuccess').innerHTML = 'Success';
        }
        debugInfo += '\nMarketplace sync status check - End\n';
        document.getElementById('txtdebuginfo').innerHTML += debugInfo;
    });
}

function servicedeskTicketsSyncCheck() {
    document.getElementById('lblTicketsLastSyncSuccess').innerHTML = '';
    document.getElementById('lblTicketsLastSyncError').innerHTML = '';
    document.getElementById('lblTicketslastsyncComments').innerHTML = '';
    var debugInfo = '\n Servicedesk ticket last sync status check - Begin';
    var ga = new GlideAjax('SakonSmokeTest');
    ga.addParam('sysparm_name', 'getTicketsLastSyncDate');
    ga.getXMLAnswer(function(answer) {
        answer = JSON.parse(answer);
        var isError = 0;
        var isWarning = 0;
        var message = '';
        var daysDifference = getDaysDifferenceNow(answer.ticketlastsyncdate);        
        if (daysDifference == 10000) {
            isWarning = 1;
            $('#lblTicketslastsyncComments').removeAttr('style');
            message = "Sync process was never run.";
            $('#lblTicketslastsyncComments').attr('style', 'color:red');
        } else if (daysDifference > 0) {
            isWarning = 1;
            message = "Last sync happened : " + Math.floor(daysDifference) + " days ago.";
        } else {
            var dateDiff = getHoursDifferenceNow(answer.ticketlastsyncdate);
            if (dateDiff == 10000) {
                isWarning = 1;
                message = "Sync process was never run.";
            } else if (dateDiff > 2) {
                isWarning = 1;
                message = "Last sync happened : " + Math.floor(dateDiff) + " hours ago.";
            }
        }

        debugInfo += '\n Servicedesk tickets last synced on: ' + answer.ticketlastsyncdate;
        //debugInfo += '\nCount of tickets last synced : ' + answer.count;//Srikanth commented because not required.
        if (answer == null) {
            document.getElementById('lblTicketsLastSyncError').innerHTML = 'Error';
        } else if (isWarning == 1) {
            document.getElementById('lblTicketsLastSyncError').innerHTML = 'Warning';
            $('#lblTicketsLastSyncError').attr('style', 'color:orange');
            $('#lblTicketslastsyncComments').removeAttr('style');
            $('#lblTicketslastsyncComments').html(message);
            $('#lblTicketslastsyncComments').attr('style', 'color:red');
        } else {
            document.getElementById('lblTicketsLastSyncSuccess').innerHTML = 'Success';
        }
        debugInfo += '\nServicedesk tickets Last sync status check - End\n';
        document.getElementById('txtdebuginfo').innerHTML += debugInfo;
    });
}

function productConfigurationCheck() {
    document.getElementById('lblSakonproductconfigurationcheckSuccess').innerHTML = '';
    document.getElementById('lblSakonproductconfigurationcheckError').innerHTML = '';
    document.getElementById('lblSakonproductconfigurationcheckComments').innerHTML = '';
    var debugInfo = '\nSakon product configuration check - Begin';
    var ga = new GlideAjax('SakonSmokeTest');
    ga.addParam('sysparm_name', 'getSakonCatalogCount');
    ga.getXML(function(response) {
        callback(response, debugInfo);
    });

    function callback(response, debugInfo) {
        var answer = response.responseXML.documentElement.getAttribute("answer");
        answer = JSON.parse(answer);

        if (answer.rawCatalogCount == 0 || answer.sNowCatalogCount == 0) {
            document.getElementById('lblSakonproductconfigurationcheckError').innerHTML = 'Error';
            debugInfo += '\nRaw catalog items count: ' + answer.rawCatalogCount;
            debugInfo += '\nServicenow catalog count: ' + answer.sNowCatalogCount;
            $('#lblSakonproductconfigurationcheckComments').html("Raw catalog items count: " + answer.rawCatalogCount + '\r\nServicenow catalog count: ' + answer.sNowCatalogCount);
            $('#lblSakonproductconfigurationcheckComments').removeAttr('style');
            $('#lblSakonproductconfigurationcheckComments').attr('style', 'color:red');

        } else {
            document.getElementById('lblSakonproductconfigurationcheckSuccess').innerHTML = 'Success';
            debugInfo += '\nRaw catalog items count: ' + answer.rawCatalogCount;
            debugInfo += '\nServicenow catalog count: ' + answer.sNowCatalogCount;
        }
        debugInfo += '\nSakon product configuration check - End\n';
        document.getElementById('txtdebuginfo').innerHTML += debugInfo;
    }
}

