    }
}

function getDaysDifferenceNow(date) {

	var syncDate = new Date(date.replace('T', ' ').replace('Z', ''));
    if (syncDate.getFullYear() <= 1901) {
        return 10000;
    }
    var now = new Date();
    var utcNowDate = now.getUTCDate();
    var syncDateDay = syncDate.getUTCDate();
    var difference = (utcNowDate - syncDateDay);
    return difference;

}

function getHoursDifferenceNow(date) {
    var syncDate = new Date(date.replace('T', ' ').replace('Z', '')); /* This is in UTC*/
    if (syncDate.getFullYear() <= 1901) {
        return 10000;
    }
    var now = new Date();
    var utcNowHour = now.getUTCHours();
    var syncDateHour = syncDate.getHours();
    var difference = (utcNowHour - syncDateHour);
    return difference;
}

