sysauto_script.script:
gs.info('>>Sakon Users>>Started Add Users to Sakon Mobile Access Group Scheduled Job');

var count = 0;
var userGR = new GlideRecord('sys_user');
userGR.addEncodedQuery('active=true^employee_numberISNOTEMPTY^emailISNOTEMPTY');
//userGR.setLimit(1);
userGR.query();
while (userGR.next()) {
    var grpToAdd = new GlideRecord('sys_user_grmember');
    grpToAdd.addQuery('group', gs.getProperty('x_sakon_mobile.sakon_access_group_id'));
    grpToAdd.addQuery('user', userGR.sys_id);
    grpToAdd.query();
    if (!grpToAdd.next()) {
        grpToAdd.initialize();
        grpToAdd.group = gs.getProperty('x_sakon_mobile.sakon_access_group_id');
        grpToAdd.user = userGR.sys_id;
        grpToAdd.insert();
		count++;
    }
}

gs.info('>>Sakon Users>> Count of users added to Sakon Mobile Access Group: ' + count);
