// (function executeRule(current, previous) {
var today = new GlideDateTime();
var reqNumber = current.getDisplayValue('request').toString();
var currentScope = gs.getCurrentScopeName();
var SyncCounter = gs.getProperty(currentScope + '.' + 'order_sync_retry_count');

var sysId;
var reqItems = [];
var grReq = new GlideRecordSecure('sc_request');
grReq.addQuery('number', reqNumber);
grReq.query();
while (grReq.next()) {
    sysId = grReq.sys_id;
    var gr = new GlideRecordSecure('sc_req_item');
    gr.addQuery('request', sysId);
    gr.query();
    while (gr.next()) {      
		var isSakonCatItem = new CommonUtilities().isSakonCatItem(current.cat_item.getDisplayValue());		
        reqItems.push(gr.sys_id.toString());
    }
}
if (reqItems.length <= 1 || current.sys_id == reqItems[0]) {    
    var gr1 = new GlideRecordSecure(currentScope + '_' + 'entity_sync_queue');
    gr1.initialize();
    gr1.servicenow_entity_id = reqNumber;
    gr1.entity_type = 'Order';
    gr1.sync_status = 0;
    gr1.retry_count_setting = SyncCounter;
    gr1.sync_counter = 0;
    gr1.last_retry_datetime = today;
    gr1.insert_datetime = today;
    gr1.modified_datetime = today;
    gr1.sync_type = 'INSERT';
    gr1.sakon_sync_action = 'INSERT';
    gr1.sakon_entity_id = '';
    gr1.insert();
}
// })(current, previous);

