var req = new GlideRecord('sc_request');
req.addQuery('sys_id', current.request); // find the REQ record
req.query();
// if REQ record found then set value
if (req.next()){
	var desc = current.variables.x_sakon_requested_for;
    req.requested_for = desc;
	req.setValue('description', 'test WF' + desc);
    req.update();
}

