answer = (function(current) {	
	var result = false;
	var state = current.state.toString();
	var state_display = current.state.getDisplayValue().toLowerCase().trim();	
	if (state=='3' || state=='4' || state=='7' || state_display == 'closed complete') {
		result = true;
	}
	
	return result;
	
})(current);

